import { all, takeLatest, call, put } from 'redux-saga/effects';
import api from '../../../services/api';
import { signInSuccess } from './actions';
import history from '../../../services/history';

export function* signIn({ payload }){

  const { email, password } = payload;

  console.tron.log(`Email: ${email} Senha: ${password}`);
  // retorna uma promisse e é assíncrono
  // const response = yield call(api.post, '/sessions',
  //   {
  //     email,
  //     password
  //   },
  //   {
  //     headers: {
  //       'Content-Type': 'application/json'
  //     }
  //   }
  // )

  function getResponse() {
    return setTimeout(()=> {
      return {
          data: {
            token: 'jkhuisehrseufhsufhilushfldshulfhshlkfhlksdhlfhlksdhlfhkljsdlkfjdlsfjlk',
            user: {
              provider: true,
              nome: 'maria',
              email: email
            }
          }
      }
    }, 5000);
  }

  const response = yield call(getResponse());

  console.tron.log(response);

  const { token, user } = response.data;

  // provider prestador de serviço
  if(!user.provider) {
    console.log('usuário não é prestador')
    return;
  }

  yield put(signInSuccess(token, user));

  history.push('/dashboard')
}

export default all ([
  takeLatest('@auth/SIGN_IN_REQUEST', signIn)
]);
